-- Đang đổ dữ liệu cho bảng `tbl_sanpham`
--

INSERT INTO `tbl_sanpham` (`sanpham_id`, `category_id`, `sanpham_name`, `sanpham_chitiet`, `sanpham_mota`, `sanpham_gia`, `sanpham_giakhuyenmai`, `sanpham_active`, `sanpham_hot`, `sanpham_soluong`, `sanpham_image`) VALUES
(49, 5, 'Smart Tivi LG 4K 55 inch', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p>Smart Tivi LG 4K 55 inch 55UQ7550PSF cung cấp m&agrave;n h&igrave;nh trải nghiệm tuyệt đẹp với h&igrave;nh ảnh độ n&eacute;t 4K, khung h&igrave;nh sống động nhờ bộ xử l&yacute; &alpha;5 Gen5 AI 4K, cảm nhận trọn vẹn t&iacute;nh nghệ thuật của bộ phim với chế độ FilmMaker Mode, &acirc;m thanh AI Sound ch&acirc;n thực, hỗ trợ LG Voice Search t&igrave;m kiếm bằng giọng n&oacute;i tiếng Việt dễ d&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<h3><strong>Tổng quan thiết kế</strong></h3>\r\n\r\n<p>-&nbsp;<a href=\"https://www.dienmayxanh.com/tivi/smart-lg-4k-55-inch-55uq7550psf\" target=\"_blank\">Smart Tivi LG 4K 55 inch 55UQ7550PSF</a>&nbsp;ho&agrave;n thiện thiết kế nội thất của mọi kh&ocirc;ng gian với vẻ ngo&agrave;i thanh lịch, đường viền tinh giản gi&uacute;p l&agrave;m nổi bật nội dung đang ph&aacute;t tr&ecirc;n m&agrave;n h&igrave;nh, cho tầm nh&igrave;n của bạn lu&ocirc;n tập trung, kh&ocirc;ng bị xao nh&atilde;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-&nbsp;Ch&acirc;n đế chắc chắn với lớp vỏ nhựa, l&otilde;i bằng kim loại, thiết kế&nbsp;<strong>kiểu</strong>&nbsp;<strong>chữ V &uacute;p ngược</strong>&nbsp;gi&uacute;p bố tr&iacute;&nbsp;<a href=\"https://www.dienmayxanh.com/tivi-lg\" target=\"_blank\">tivi LG</a>&nbsp;thăng bằng tr&ecirc;n kệ, hạn chế nghi&ecirc;ng, đổ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Tạo điểm nhấn ho&agrave;n hảo cho căn ph&ograve;ng kh&aacute;ch, ph&ograve;ng ngủ, ph&ograve;ng l&agrave;m việc c&oacute; diện t&iacute;ch vừa v&agrave; lớn với&nbsp;<a href=\"https://www.dienmayxanh.com/tivi-lg?g=55-inch\" target=\"_blank\">m&agrave;n h&igrave;nh 55 inch</a>.</p>\r\n', '10490000', '16900000', 1, 0, 20, 'smart-lg-4k-55-inch.jpg'),
(50, 5, 'Google Tivi Sony 4K 43 inch', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tivi/android-sony-4k-43-inch-kd-43x80k\" target=\"_blank\">Google Tivi Sony 4K 43 inch KD-43X80K</a>&nbsp;g&acirc;y ấn tượng với thiết kế tinh tế, h&igrave;nh ảnh sắc n&eacute;t, ch&acirc;n thật, &acirc;m thanh cuốn h&uacute;t, hỗ trợ nhiều kết nối, t&iacute;nh năng cho từng ph&uacute;t gi&acirc;y trải nghiệm của bạn đều tuyệt vời v&agrave; th&uacute; vị.&nbsp;</p>\r\n', '<h3><strong>Thiết kế</strong></h3>\r\n\r\n<p>-&nbsp;<strong><a href=\"https://www.dienmayxanh.com/tivi-sony-google-tivi\" target=\"_blank\">Google tivi Sony</a>&nbsp;</strong>c&oacute; thiết kế tinh tế với&nbsp;<strong>đường viền si&ecirc;u mỏng</strong>,&nbsp;<strong>ch&acirc;n đế chữ V bằng nhựa c&oacute; v&acirc;n &uacute;p ngược</strong>&nbsp;chắc chắn, tạo điểm nhấn cho kh&ocirc;ng gian th&ecirc;m sang trọng, hiện đại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-&nbsp;<strong><a href=\"https://www.dienmayxanh.com/tivi-sony?g=43-inch\" target=\"_blank\">Tivi Sony 43 inch</a></strong>&nbsp;ph&ugrave; hợp cho nhiều loại kh&ocirc;ng gian, kể cả những nơi c&oacute; diện t&iacute;ch kh&ocirc;ng qu&aacute; lớn như ph&ograve;ng ngủ, ph&ograve;ng kh&aacute;ch nhỏ, ph&ograve;ng kh&aacute;ch sạn,...</p>\r\n', '14900000', '13900000', 1, 1, 20, 'android-sony-4k-43-inch.png'),
(51, 5, 'Tivi Casper 4K 55 inch ', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p>Smart Tivi Casper 4K 55 inch 55UW6000 sở hữu m&agrave;n h&igrave;nh tr&agrave;n viền thanh mảnh, tạo khung h&igrave;nh rộng v&agrave; cảm gi&aacute;c tinh tế cho kh&ocirc;ng gian lắp đặt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tivi-casper?g=55-inch\" target=\"_blank\">Tivi Casper 55 inch</a>&nbsp;ch&acirc;n đế chữ V &uacute;p ngược dễ th&aacute;o rời d&ugrave;ng đẹp cả khi đặt kệ tủ hay treo tường, sử dụng ph&ugrave; hợp c&aacute;c ph&ograve;ng c&oacute; diện t&iacute;ch trung b&igrave;nh v&agrave; lớn như ph&ograve;ng kh&aacute;ch, ph&ograve;ng họp,&hellip;</p>\r\n', '<h3><strong>Thưởng thức h&igrave;nh ảnh c&oacute; độ tương phản cao, sống động v&agrave; ch&acirc;n thực với c&ocirc;ng nghệ 4K HDR10</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tivi-casper\" target=\"_blank\">Tivi Casper</a>&nbsp;trang bị c&ocirc;ng nghệ 4K HDR10 với khả năng t&aacute;i tạo, tăng cường tương phản giữa v&ugrave;ng s&aacute;ng v&agrave; v&ugrave;ng tối tr&ecirc;n khung h&igrave;nh, gi&uacute;p v&ugrave;ng tối đen hơn, v&ugrave;ng s&aacute;ng tinh khiết hơn, h&igrave;nh ảnh c&oacute; chiều s&acirc;u v&agrave; cảm gi&aacute;c ch&acirc;n thực hơn, để dễ d&agrave;ng l&ocirc;i cuốn người xem v&agrave;o từng nội dung được theo d&otilde;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Hệ điều h&agrave;nh&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/webos-tren-tivi-lg-la-gi-581001\" target=\"_blank\">webOS</a>&nbsp;5.0 c&oacute; giao diện phẳng, th&acirc;n thiện v&agrave; dễ sử dụng</strong></h3>\r\n\r\n<p>Hệ điều h&agrave;nh webOS 5.0 hiện đại c&oacute; giao diện đơn giản, dễ thao t&aacute;c, sở hữu kho ứng dụng phong ph&uacute; với h&agrave;ng loạt ứng dụng quen thuộc như: YouTube, Netflix, FPT Play, ZingTV, Clip TV, Nhaccuatui,... đ&aacute;p ứng nhu cầu giải tr&iacute; cho mọi th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh.</p>\r\n', '13900000', '7990000', 1, 1, 20, 'smart-casper-4k-55-inch.jpg'),
(52, 5, 'Tivi Samsung 4K 65 inch', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tivi/led-4k-samsung-ua65au8100\" target=\"_blank\">Smart Tivi Samsung 4K 65 inch UA65AU8100</a>&nbsp;c&oacute; thiết kế Airslim kh&ocirc;ng viền 3 cạnh sang trọng v&agrave; tinh tế. Mang lại tổng thể cho kh&ocirc;ng gian trưng b&agrave;y th&ecirc;m điểm nhấn v&ocirc; c&ugrave;ng ấn tượng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.dienmayxanh.com/tivi-samsung?g=65-inch\" target=\"_blank\">tivi Samsung 65 inch</a>&nbsp;c&ugrave;ng ch&acirc;n đế vững chắc ph&ugrave; hợp trưng b&agrave;y c&aacute;c kh&ocirc;ng gian như: Ph&ograve;ng kh&aacute;ch, ph&ograve;ng ngủ, ph&ograve;ng họp,...</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<h3><strong>Khả năng tự động điều chỉnh m&agrave;u sắc nhờ c&ocirc;ng nghệ bộ xử l&yacute; Crystal 4K</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tivi-samsung\" target=\"_blank\">Tivi Samsung</a>&nbsp;sở hữu bộ xử l&yacute; Crystal 4K với khả năng tự động điều chỉnh m&agrave;u sắc cho khả năng tối ưu độ tương phản, dải m&agrave;u sắc rộng. Gi&uacute;p người d&ugrave;ng c&oacute; thể xem được h&igrave;nh ảnh hiển thị với chất lượng tuyệt vời nhất.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>H&igrave;nh ảnh được tăng cường độ tương phản, tối ưu trải nghiệm trong từng khung h&igrave;nh khi kết hợp c&ocirc;ng nghệ HDR v&agrave; Contrast Enhancer</strong></h3>\r\n\r\n<p><strong>C&ocirc;ng nghệ HDR</strong>&nbsp;c&oacute; khả năng tăng th&ecirc;m độ tương phản v&agrave; m&agrave;u sắc cho h&igrave;nh ảnh.</p>\r\n\r\n<p><strong>Contrast Enhancer&nbsp;</strong>gi&uacute;p tối ưu từng khung h&igrave;nh hiển thị tr&ecirc;n&nbsp;<a href=\"https://www.dienmayxanh.com/tivi\" target=\"_blank\">tivi</a>, gi&uacute;p người d&ugrave;ng c&oacute; thể xem được những h&igrave;nh ảnh sống động như thật.</p>\r\n', '16400000', '12490000', 0, 0, 20, 'led-4k-samsung.jpg'),
(53, 5, 'Tivi Toshiba 4K 55 inch', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tivi/google-tivi-toshiba-4k-55-inch-55c350lp\" target=\"_blank\">Google Tivi Toshiba 4K 55 inch 55C350LP</a>&nbsp;hiển thị h&igrave;nh ảnh 4K r&otilde; n&eacute;t với&nbsp;bộ xử l&yacute; Regza Engine 4K hiệu suất cao, c&ocirc;ng nghệ&nbsp;Ultra Essential PQ t&aacute;i tạo khung h&igrave;nh chi tiết, sinh động,&nbsp;AI 4K Upscaling n&acirc;ng cấp chất lượng khung h&igrave;nh đến gần 4K,&nbsp;c&ocirc;ng nghệ Dolby Vision cho trải nghiệm điện ảnh đ&iacute;ch thực, c&ocirc;ng nghệ&nbsp;Dolby Atmos cho &acirc;m thanh đa chiều mạnh mẽ, t&igrave;m kiếm bằng giọng n&oacute;i tiếng Việt với Google Assistant&nbsp;dễ d&agrave;ng.</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<p>-&nbsp;Toshiba 55C350LP&nbsp;c&oacute; vẻ ngo&agrave;i thời thượng, thiết kế mỏng đều, cho&nbsp;<a href=\"https://www.dienmayxanh.com/tivi\" target=\"_blank\">tivi</a>&nbsp;kết nối liền mạch với mọi phối cảnh, tạo n&ecirc;n điểm nhấn sang trọng cho kh&ocirc;ng gian ph&ograve;ng kh&aacute;ch, ph&ograve;ng ngủ, ph&ograve;ng hội nghị của bạn. M&agrave;n h&igrave;nh tr&agrave;n viền cho phạm vi hiển thị nội dung mở rộng, mang đến tầm nh&igrave;n v&ocirc; tận tựa như trải nghiệm rạp chiếu phim đ&iacute;ch thực.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-&nbsp;<a href=\"https://www.dienmayxanh.com/tivi?g=55-inch\" target=\"_blank\">M&agrave;n h&igrave;nh 55 inch</a>&nbsp;lựa chọn th&iacute;ch hợp cho c&aacute;c căn ph&ograve;ng c&oacute; diện t&iacute;ch vừa v&agrave; lớn. Thiết kế chống lật đảm bảo ngay cả khi xảy ra va chạm mạnh th&igrave; tivi cũng kh&ocirc;ng rơi, ng&atilde;,&nbsp;gi&uacute;p đảm bảo độ ổn định, chống chịu tốt cho thiết bị khi lắp treo tường, nhờ đ&oacute;, gi&uacute;p bảo vệ cho gia đ&igrave;nh c&oacute; trẻ nhỏ tinh nghịch tối ưu.</p>\r\n', '15690000', '9990000', 0, 0, 20, 'google-tivi-toshiba-4k-55-inch.jpg'),
(54, 5, 'Tivi Casper 43 inch', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tivi/casper-43fx6200\" target=\"_blank\">Smart Tivi Casper 43 inch 43FX6200</a>&nbsp;được thiết kế với m&agrave;n h&igrave;nh tr&agrave;n viền ba cạnh sang trọng, mang lại trải nghiệm v&ocirc; c&ugrave;ng ho&agrave;n hảo v&agrave; ấn tượng. Ch&acirc;n đế chữ V &uacute;p ngược gi&uacute;p tivi đứng vững v&agrave;ng v&agrave; ổn định.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tivi-casper?g=43-inch\" target=\"_blank\">Tivi Casper 43 inch</a>&nbsp;gọn g&agrave;ng, ph&ugrave; hợp đặt ở c&aacute;c vị tr&iacute; như: ph&ograve;ng kh&aacute;ch, ph&ograve;ng ngủ, văn ph&ograve;ng nhỏ...</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>M&agrave;u sắc h&igrave;nh ảnh r&otilde; n&eacute;t, rực rỡ từ mọi g&oacute;c nh&igrave;n nhờ tấm nền IPS</h3>\r\n\r\n<p>Tivi được trang bị tấm nền IPS, gi&uacute;p m&agrave;u sắc h&igrave;nh ảnh r&otilde; n&eacute;t từ mọi g&oacute;c nh&igrave;n. Đặc biệt, tối ưu g&oacute;c nh&igrave;n rộng đến 178 độ v&agrave; chuyển động h&igrave;nh tr&ecirc;n tivi v&ocirc; c&ugrave;ng mượt m&agrave;. Tuổi thọ tấm nền cao, gi&uacute;p người d&ugrave;ng c&oacute; trải nghiệm xem tivi ấn tượng trong thời gian d&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>M&agrave;u sắc h&igrave;nh ảnh r&otilde; n&eacute;t, rực rỡ từ mọi g&oacute;c nh&igrave;n nhờ tấm nền IPS</h3>\r\n\r\n<p>Tivi được trang bị tấm nền IPS, gi&uacute;p m&agrave;u sắc h&igrave;nh ảnh r&otilde; n&eacute;t từ mọi g&oacute;c nh&igrave;n. Đặc biệt, tối ưu g&oacute;c nh&igrave;n rộng đến 178 độ v&agrave; chuyển động h&igrave;nh tr&ecirc;n tivi v&ocirc; c&ugrave;ng mượt m&agrave;. Tuổi thọ tấm nền cao, gi&uacute;p người d&ugrave;ng c&oacute; trải nghiệm xem tivi ấn tượng trong thời gian d&agrave;i.</p>\r\n', '7590000', '5590000', 0, 0, 20, 'casper.jpg'),
(55, 5, 'Tivi Samsung 4K 55 inch', '<h3><strong>M&ocirc; tả sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tivi/qled-4k-samsung-qa55q65a\" target=\"_blank\">Smart Tivi QLED 4K 55 inch Samsung QA55Q65A</a>&nbsp;với thiết kế với m&agrave;n h&igrave;nh tr&agrave;n viền 4 cạnh, cho người d&ugrave;ng trải nghiệm khung h&igrave;nh giải tr&iacute; tr&ecirc;n tivi ch&acirc;n thực như thực tế đang xảy ra trước mắt.</p>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tivi-samsung?g=55-inch\" target=\"_blank\">Tivi Samsung 55 inch</a>&nbsp;c&oacute; ch&acirc;n đế được thiết kế gọn g&agrave;ng, vững chắc. Mang lại sự thanh lịch, sang trọng cho chiếc tivi, ph&ugrave; hợp trưng b&agrave;y ở ph&ograve;ng kh&aacute;ch, ph&ograve;ng ngủ,...</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>M&agrave;u sắc hiển thị rực rỡ, tự nhi&ecirc;n với khả năng hiển thị 100% dải m&agrave;u nhờ m&agrave;n h&igrave;nh chấm lượng tử Quantum Dot (QLED)</h3>\r\n\r\n<p>Samsung trang bị cho chiếc&nbsp;<a href=\"https://www.dienmayxanh.com/tivi?g=smart-tivi\" target=\"_blank\">Smart tivi</a>&nbsp;n&agrave;y c&ocirc;ng nghệ m&agrave;n h&igrave;nh chấm lượng tử Quantum Dot với khả năng hiển thị 100% dải sắc m&agrave;u, gi&uacute;p người d&ugrave;ng c&oacute; thể thưởng thức được những h&igrave;nh ảnh tr&ecirc;n tivi với m&agrave;u sắc v&ocirc; c&ugrave;ng rực rỡ v&agrave; sống động.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>H&igrave;nh ảnh được tăng cường độ tương phản nhờ c&ocirc;ng nghệ Quantum HDR</h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tivi-samsung\" target=\"_blank\">Tivi Samsung</a>&nbsp;với c&ocirc;ng nghệ Quantum HDR gi&uacute;p h&igrave;nh ảnh tăng cường độ tương phản, độ chi tiết h&igrave;nh ảnh. Người d&ugrave;ng c&oacute; thể thưởng thức được những cảnh quay v&ocirc; c&ugrave;ng ho&agrave;n hảo v&agrave; ấn tượng</p>\r\n', '15400000', '12900000', 0, 1, 20, 'qled-4k-samsung.jpg'),
(56, 5, 'Google Tivi Sony 4K 43 inch', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tivi/google-sony-4k-43-inch-kd-43x75k\" target=\"_blank\">Google Tivi Sony 4K 43 inch KD-43X75K</a>&nbsp;được trang bị đa dạng c&aacute;c c&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh, &acirc;m thanh hiện đại, c&ugrave;ng với c&aacute;c t&iacute;nh năng th&ocirc;ng minh ti&ecirc;n tiến,&nbsp;<a href=\"https://www.dienmayxanh.com/tivi-sony\" target=\"_blank\">tivi Sony</a>&nbsp;cho bạn c&oacute; những trải nghiệm tuyệt vời tr&ecirc;n chiếc tivi n&agrave;y.</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Thiết kế</h3>\r\n\r\n<p>-&nbsp;<a href=\"https://www.dienmayxanh.com/tivi-google-tivi\" target=\"_blank\">Google tivi Sony</a>&nbsp;được thiết kế liền mạch, kiểu d&aacute;ng tối giản gi&uacute;p l&agrave;m nổi bật m&agrave;n h&igrave;nh của tivi 43X75K, cho người xem ho&agrave;n to&agrave;n đắm ch&igrave;m v&agrave;o nội dung đang được tr&igrave;nh chiếu.</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.dienmayxanh.com/tivi-sony?g=43-inch\" target=\"_blank\"><strong>tivi Sony 43 inch</strong></a>&nbsp;được l&agrave;m phẳng, k&iacute;ch thước vừa đủ với kh&ocirc;ng gian ph&ograve;ng kh&aacute;ch, ph&ograve;ng ngủ, ph&ograve;ng họp. Ch&acirc;n đế chữ V &uacute;p ngược mỏng, bằng nhựa &aacute;nh đen cuốn h&uacute;t, tivi bố tr&iacute; thanh lịch cho kh&ocirc;ng gian lắp đặt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>C&ocirc;ng nghệ h&igrave;nh ảnh</h3>\r\n\r\n<p>- Hiển thị h&igrave;nh ảnh sinh động với&nbsp;<strong>độ ph&acirc;n giải 4K</strong>&nbsp;n&eacute;t gấp 4 lần Full HD.</p>\r\n\r\n<p>-&nbsp;<strong>Bộ xử l&yacute; X1 4K HDR</strong>&nbsp;tinh chỉnh c&aacute;c yếu tố của h&igrave;nh ảnh, giảm nhiễu, tăng chi tiết, m&agrave;u sắc thể hiện trọn vẹn, n&acirc;ng cấp h&igrave;nh ảnh gần chuẩn tương đương 4K mang đến trải nghiệm ch&acirc;n thực mỗi khi mở tivi l&ecirc;n.&nbsp;</p>\r\n\r\n<p>-<strong>&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/cac-cong-nghe-quet-hinh-tren-tivi-sony-2018-1108239#hmenuid3\" target=\"_blank\">Motionflow XR 200</a></strong>&nbsp;với khả năng ch&egrave;n khung nền đen gi&uacute;p giảm độ mờ, h&igrave;nh ảnh chuyển động mượt m&agrave; tr&ecirc;n từng khung h&igrave;nh.</p>\r\n', '11400000', '9650000', 0, 0, 20, 'google-sony-4k-43-inch.jpg'),
(57, 5, 'Tivi QLED TCL 4K 43 inch', '<p>&nbsp;</p>\r\n\r\n<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p>Google Tivi QLED TCL 4K 43 inch 43Q636​&nbsp;mang đến trải nghiệm xem ho&agrave;n hảo với thiết kế tr&agrave;n viền, tận hưởng nội dung chuẩn điện ảnh với bộ đ&ocirc;i Dolby Vision v&agrave; Dolby Atmos, c&oacute; hệ điều h&agrave;nh&nbsp;Google TV dễ sử dụng, tivi t&iacute;ch hợp sẵn micro cho điều khiển giọng n&oacute;i rảnh tay tiện lợi.&nbsp;</p>\r\n', '<p><strong>Chi tiết sản phẩm</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Tổng quan thiết kế</h3>\r\n\r\n<p>-&nbsp;<a href=\"https://www.dienmayxanh.com/tivi/google-tcl-4k-43-inch-43q636\">Google Tivi TCL 4K 43 inch 43Q636</a>&nbsp;thiết kế tr&agrave;n viền, m&agrave;n h&igrave;nh phẳng cho tầm nh&igrave;n trải rộng để bạn ho&agrave;n to&agrave;n đắm ch&igrave;m trong nội dung đang tr&igrave;nh chiếu tr&ecirc;n m&agrave;n h&igrave;nh tivi.</p>\r\n\r\n<p>- Đường viền v&agrave;&nbsp;<strong>ch&acirc;n đế kiểu chữ V &uacute;p ngược</strong>&nbsp;l&agrave;m bằng kim loại cứng c&aacute;p, tăng cường bảo vệ m&agrave;n h&igrave;nh v&agrave; n&acirc;ng đỡ tivi thăng bằng khi bố tr&iacute; tr&ecirc;n kệ, b&agrave;n.&nbsp;</p>\r\n\r\n<p>- M&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.dienmayxanh.com/tivi-google-tivi\" target=\"_blank\">Google Tivi</a>&nbsp;k&iacute;ch cỡ<strong>&nbsp;<a href=\"https://www.dienmayxanh.com/tivi-tcl?g=43-inch\" target=\"_blank\">43 inch</a></strong>, trang tr&iacute; cho kh&ocirc;ng gian nội thất của ph&ograve;ng l&agrave;m việc, ph&ograve;ng kh&aacute;ch, ph&ograve;ng ngủ c&oacute; diện t&iacute;ch vừa v&agrave; nhỏ h&agrave;i h&ograve;a, sang trọng.</p>\r\n', '1190000', '7990000', 0, 0, 20, 'google-tcl-4k-43-inch-.jpg'),
(58, 2, 'Tủ lạnh Samsung Inverter 236 lít', '<h3><strong>M&ocirc; tả sản phẩm</strong></h3>\r\n\r\n<p>Tủ lạnh Samsung Inverter 236 l&iacute;t&nbsp;RT22M4032BY/SV c&oacute; bộ lọc Deodorizer kh&aacute;ng khuẩn, loại bỏ mồ h&ocirc;i kh&oacute; chịu, với ngăn Optimal Fresh Zone -1 độ C gi&uacute;p chế biến kh&ocirc;ng cần r&atilde; đ&ocirc;ng. Duy tr&igrave; nhiệt độ, độ ẩm ổn định nhờ c&ocirc;ng nghệ All Around Cooling,...</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<h3>Thịt c&aacute; tươi ngon, ăn trong ng&agrave;y kh&ocirc;ng cần r&atilde; đ&ocirc;ng với&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/cac-diem-noi-bat-tren-tu-lanh-samsung-2018-1116370#optimal-fresh-zone\" target=\"_blank\">ngăn đ&ocirc;ng mềm -1 độ C Optimal Fresh Zone</a></h3>\r\n\r\n<p>Ngăn đ&ocirc;ng mềm thực phẩm ở nhiệt độ -1&deg;C của&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh\" target=\"_blank\">tủ lạnh</a>&nbsp;gi&uacute;p thịt c&aacute; lu&ocirc;n tươi ngon, trọn vẹn dinh dưỡng do ch&uacute;ng kh&ocirc;ng bị đ&ocirc;ng đ&aacute;, gi&uacute;p bạn c&oacute; thể chế biến thực phẩm nhanh ch&oacute;ng kh&ocirc;ng cần r&atilde; đ&ocirc;ng.</p>\r\n\r\n<p>Bạn n&ecirc;n sử dụng ngăn n&agrave;y đối với thực phẩm sẽ chế biến trong ng&agrave;y, nếu muốn trữ l&acirc;u hơn, bạn h&atilde;y d&ugrave;ng ngăn đ&ocirc;ng để bảo quản được tốt nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Tiết kiệm điện năng, vận h&agrave;nh &ecirc;m &aacute;i v&agrave; bền bỉ với c&ocirc;ng nghệ&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh-inverter\" target=\"_blank\">Digital Inverter</a>&nbsp;hiện đại</h3>\r\n\r\n<p>C&ocirc;ng nghệ Digital Inverter gi&uacute;p cho&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh-samsung\" target=\"_blank\">tủ lạnh Samsung</a>&nbsp;của bạn vừa tiết kiệm điện năng ti&ecirc;u thụ, vừa vận h&agrave;nh thật &ecirc;m &aacute;i v&agrave; bền bỉ.</p>\r\n', '9390000', '5690000', 1, 1, 20, 'samsung.jpg'),
(59, 2, 'Tủ lạnh LG Inverter 217 Lít GV-B212WB ', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tu-lanh/tu-lanh-lg-gv-b212wb\" target=\"_blank\">Tủ lạnh LG Inverter 217 L&iacute;t GV-B212WB</a>&nbsp;c&oacute; khả năng tiết kiệm điện v&agrave; duy tr&igrave; nhiệt độ ổn định gi&uacute;p thực phẩm lu&ocirc;n tươi ngon. Hơn nữa, tủ lạnh c&ograve;n c&oacute; thể l&agrave;m lạnh nhanh v&agrave; duy tr&igrave; c&aacute;c loại đồ uống ở vị tr&iacute; c&aacute;nh cửa tủ một c&aacute;ch tối ưu.</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<p>- Tủ lạnh LG Inverter 217 L&iacute;t GV-B212WB thuộc kiểu&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh?g=ngan-da-tren\" target=\"_blank\">tủ ngăn đ&aacute; tr&ecirc;n</a>&nbsp;với chất liệu cửa được l&agrave;m bằng&nbsp;<strong>th&eacute;p kh&ocirc;ng gỉ m&agrave;u đen</strong>&nbsp;c&oacute; độ bền tốt, hạn chế trầy xước v&agrave; c&oacute; thể phối hợp h&agrave;i h&ograve;a với những nội thất kh&aacute;c b&ecirc;n trong gian bếp nh&agrave; bạn.</p>\r\n\r\n<p>- Với dung t&iacute;ch&nbsp;<strong>217 l&iacute;t</strong>, mẫu&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh-lg\" target=\"_blank\">tủ lạnh LG</a>&nbsp;n&agrave;y c&oacute; thể đ&aacute;p ứng tốt cho những gia đ&igrave;nh từ&nbsp;<strong>2 - 3 người</strong>.</p>\r\n', '8990000', '5990000', 1, 1, 20, 'tu-lanh-lg.jpg'),
(60, 2, 'Tủ lạnh LG Inverter 266 Lít GV-B262WB', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tu-lanh/tu-lanh-lg-inverter-266-lit-gv-b262wb\" target=\"_blank\">Tủ lạnh LG Inverter 266 l&iacute;t GV-B262WB</a>&nbsp;c&oacute; khả năng tiết kiệm điện hiệu quả v&agrave; vận h&agrave;nh &ecirc;m &aacute;i. Hơn nữa, mẫu tủ lạnh n&agrave;y c&oacute; thể duy tr&igrave; được độ tươi ngon thực phẩm v&agrave; l&agrave;m lạnh đồ uống tối ưu khi đặt ở ph&iacute;a c&aacute;nh cửa tủ lạnh nhờ c&aacute;c c&ocirc;ng nghệ đặc biệt.</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<p>- GV-B262WB thuộc mẫu&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh?g=ngan-da-tren\" target=\"_blank\">tủ lạnh ngăn đ&aacute; tr&ecirc;n</a>&nbsp;2 c&aacute;nh với chất liệu cửa tủ được l&agrave;m bằng&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/cac-chat-lieu-cua-tu-lanh-pho-bien-hien-nay-tren-t-1177203#thep\" target=\"_blank\">th&eacute;p kh&ocirc;ng gỉ</a>&nbsp;s&aacute;ng b&oacute;ng v&agrave; c&oacute; độ bền tốt.</p>\r\n\r\n<p>- Nhờ sở hữu&nbsp;<strong>gam m&agrave;u đen&nbsp;</strong>sang trọng,&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh-lg\" target=\"_blank\">tủ lạnh LG</a>&nbsp;n&agrave;y c&oacute; thể phối hợp h&agrave;i h&ograve;a với c&aacute;c nội thất kh&aacute;c trong khu vực gian bếp một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p>- Với&nbsp;<strong>dung t&iacute;ch 266 l&iacute;t</strong>, tủ lạnh LG GV-B262WB c&oacute; thể đ&aacute;p ứng tốt cho nhu cầu sử dụng của c&aacute;c hộ gia đ&igrave;nh từ&nbsp;<strong>2 - 3 người</strong>&nbsp;hoặc những ai đang sống 1 m&igrave;nh nhưng lại c&oacute; nhu cầu bảo quản số lượng lớn thực phẩm để c&oacute; thể d&ugrave;ng được cả tuần.</p>\r\n', '10990000', '6990000', 1, 0, 20, 'tu-lanh-lg-inverter-266.jpg'),
(61, 2, 'Tủ lạnh Toshiba Inverter 180 lít GR-B22VU UKG', '<h3><strong>M&ocirc; tả sản phẩm</strong></h3>\r\n\r\n<p>Với kiểu&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh?g=ngan-da-tren\" target=\"_blank\">tủ ngăn đ&aacute; tr&ecirc;n</a>, chiếc&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh/toshiba-gr-b22vu-ukg\" target=\"_blank\">tủ lạnh Toshiba Inverter 180 l&iacute;t GR-B22VU UKG</a>&nbsp;được thiết kế nhỏ gọn nhưng vẫn to&aacute;t l&ecirc;n vẻ đẹp hiện đại bởi t&ocirc;ng m&agrave;u đen tuyền sang trọng. Dễ d&agrave;ng đặt ở bất k&igrave; vị tr&iacute; n&agrave;o trong nh&agrave;, từ ph&ograve;ng kh&aacute;ch đến ph&ograve;ng bếp.</p>\r\n\r\n<p>Chiếc tủ lạnh Toshiba n&agrave;y chỉ sở hữu dung t&iacute;ch 180 l&iacute;t, ph&ugrave; hợp với gia đ&igrave;nh nhỏ tầm 2 &ndash; 3 người nhưng sẽ l&agrave; nội thất bắt mắt trong kh&ocirc;ng gian nh&agrave; bạn.</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<h3>Si&ecirc;u tiết kiệm điện với&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/tu-lanh-inverter-la-gi-585937\" target=\"_blank\">c&ocirc;ng nghệ biến tần Inverter</a></h3>\r\n\r\n<p>Nhờ c&oacute; sự hỗ trợ của c&ocirc;ng nghệ biến tần Inverter,&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh-toshiba\" target=\"_blank\">tủ lạnh Toshiba</a>&nbsp;GR-B22VU UKG sẽ gi&uacute;p bạn bớt lo lắng về tiền điện mỗi th&aacute;ng, v&igrave; mang lại khả năng tiết kiệm điện hiệu quả so với tủ lạnh th&ocirc;ng thường.</p>\r\n\r\n<p>C&ocirc;ng nghệ biến tần n&agrave;y sẽ kiểm so&aacute;t sự hoạt động của m&aacute;y n&eacute;n gi&uacute;p duy tr&igrave; nhiệt độ b&ecirc;n trong tủ v&agrave; hạn chế tiếng ồn đến mức tối đa.</p>\r\n\r\n<h3>Bảo quản lạnh thực phẩm to&agrave;n diện nhờ&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/cong-nghe-lam-lanh-tren-tu-lanh-toshiba-585931#khi-lanh-vong-cung\" target=\"_blank\">hệ thống kh&iacute; lạnh v&ograve;ng cung</a></h3>\r\n\r\n<p>C&ocirc;ng nghệ l&agrave;m lạnh thực phẩm bằng luồng kh&iacute; v&ograve;ng cung gi&uacute;p hơi lạnh được lưu chuyển đến mọi ng&oacute;c ng&aacute;ch trong&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh\" target=\"_blank\">tủ lạnh</a>&nbsp;cũng như khắc phục được t&igrave;nh trạng thực phẩm bị đ&ocirc;ng đ&aacute; (do qu&aacute; lạnh) hay bị hư hao (do thiếu độ lạnh).</p>\r\n\r\n<p>&nbsp;</p>\r\n', '5590000', '5290000', 0, 0, 20, 'toshiba-gr.jpg'),
(62, 2, 'Tủ lạnh Aqua Inverter 189 lít AQR-T220FA', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tu-lanh/tu-lanh-aqua-aqr-t220fa-fb\" target=\"_blank\">Tủ lạnh Aqua Inverter 189 l&iacute;t AQR-T220FA(FB)</a>&nbsp;c&oacute; khả năng l&agrave;m lạnh đa chiều, gi&uacute;p bảo quản thực phẩm tối ưu, giảm thiểu tỷ lệ hư hỏng. Ngo&agrave;i ra, tủ lạnh cũng mang lại hiệu quả tiết kiệm điện nhờ sử dụng c&ocirc;ng nghệ Twin Inverter.</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<p>-&nbsp;<strong>Aqua Inverter 189 l&iacute;t AQR-T220FA(FB)</strong>&nbsp;được thiết kế dạng&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh-ngan-da-tren\" target=\"_blank\">tủ lạnh ngăn đ&aacute; tr&ecirc;n</a>&nbsp;với mặt cửa tủ l&agrave;m bằng&nbsp;<strong>chất liệu th&eacute;p bền bỉ</strong>, đồng thời phủ&nbsp;<strong>sơn m&agrave;u đen sang trọng</strong>&nbsp;n&ecirc;n tủ lạnh ph&ugrave; hợp lắp đặt ở mọi vị tr&iacute; b&ecirc;n trong khu vực nh&agrave; bếp.</p>\r\n\r\n<p>- Dung t&iacute;ch sử dụng của mẫu&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh-aqua-sanyo\" target=\"_blank\">tủ lạnh Aqua</a>&nbsp;n&agrave;y khoảng&nbsp;<strong>189 l&iacute;t</strong>, đ&aacute;p ứng khả năng lưu trữ thực phẩm cho gia đ&igrave;nh từ&nbsp;<strong>2 - 3 người sử dụng</strong>.</p>\r\n', '5490000', '5290000', 0, 0, 20, 'tu-lanh-aqua-aqr.jpg'),
(63, 2, 'Tủ lạnh Samsung Inverter 488 lít RF48A4000B4/SV', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p>Tủ lạnh Samsung Inverter 488 l&iacute;t RF48A4000B4/SV với bộ lọc kh&aacute;ng khuẩn Deodorizer gi&uacute;p loại bỏ m&ugrave;i h&ocirc;i kh&oacute; chịu, thực phẩm kh&ocirc;ng lẫn m&ugrave;i, tươi ngon nhờ Twin Cooling Plus. Thiết kế Multi - Door sang trọng, hiện đại, tr&aacute;nh l&atilde;ng ph&iacute; hơi lạnh. Tiết kiện 50% điện năng nhờ c&ocirc;ng nghệ Digital Inverter</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<p>L&agrave;m lạnh đồng đều với&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh?g=hai-dan-lanh-doc-lap-ngan-lan-mui\" target=\"_blank\">c&ocirc;ng nghệ 2 d&agrave;n lạnh độc lập Twin Cooling Plus</a></p>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tu-lanh-samsung\" target=\"_blank\">Tủ lạnh Samsung</a>&nbsp;được trang bị 2 d&agrave;n lạnh độc lập ở ngăn đ&ocirc;ng v&agrave; ngăn lạnh, ngăn luồng kh&iacute; lạnh x&acirc;m nhập lẫn nhau v&agrave; cho ph&eacute;p điều chỉnh nhiệt tại hai ngăn ri&ecirc;ng biệt, mang đến độ ẩm ph&ugrave; hợp v&agrave; giữ trọn vẹn hương vị, dưỡng chất của thực phẩm.</p>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<h3>Bảo quản thực phẩm kh&ocirc;ng cần r&atilde; đ&ocirc;ng nhờ c&ocirc;ng nghệ No Frost</h3>\r\n\r\n<p>Nhờ c&ocirc;ng nghệ No Frost kh&iacute; lạnh được lưu th&ocirc;ng v&agrave; tỏa đều khắp ng&oacute;c ng&aacute;ch trong tủ, loại bỏ khả năng l&agrave;m đ&ocirc;ng thực phẩm m&agrave; vẫn đảm bảo thực phẩm được bảo quản tốt. Đồng thời, gi&uacute;p ti&ecirc;u thụ điện năng ổn định.</p>\r\n', '23990000', '18990000', 0, 1, 20, 'samsung-.jpg'),
(64, 2, 'Tủ lạnh Samsung Inverter 307 lít RB30N4190BY/SV', '<h3><strong>M&ocirc; tả sản phẩm</strong></h3>\r\n\r\n<p>Tủ lạnh Samsung Inverter 307 l&iacute;t RB30N4190BY/SV&nbsp;với bộ lọc kh&aacute;ng khuẩn Deodorizer gi&uacute;p loại bỏ m&ugrave;i h&ocirc;i kh&oacute; chịu, thực phẩm kh&ocirc;ng lẫn m&ugrave;i, tươi ngon nhờ Twin Cooling Plus. Thiết kế Multi - Door sang trọng, hiện đại, tr&aacute;nh l&atilde;ng ph&iacute; hơi lạnh. Tiết kiện 50% điện năng nhờ c&ocirc;ng nghệ Digital Inverter</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<h3>Tiết kiệm điện năng hiệu quả với&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/cong-nghe-digital-inverter-tren-tu-lanh-samsung-co-1022075\" target=\"_blank\">c&ocirc;ng nghệ Digital Inverter</a></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tu-lanh-samsung\" target=\"_blank\">Tủ lạnh Samsung</a>&nbsp;sử dụng m&aacute;y n&eacute;n với c&ocirc;ng nghệ Digital Inverter tự động điều chỉnh 5 cấp độ theo nhu cầu của người sử dụng. Qua đ&oacute;, l&agrave;m giảm tiếng ồn, tiết kiệm điện năng sử dụng v&agrave; tăng tuổi thọ của m&aacute;y n&eacute;n.</p>\r\n\r\n<h3>L&agrave;m lạnh tốt hơn với&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/cac-diem-noi-bat-tren-tu-lanh-samsung-1116370#lam-lanh-vom\" target=\"_blank\">c&ocirc;ng nghệ l&agrave;m lạnh dạng v&ograve;m</a></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tu-lanh/samsung-inverter-307-lit-rb30n4190by-sv\" target=\"_blank\">Tủ lạnh Samsung Inverter 307 l&iacute;t RB30N4190BY/SV</a>&nbsp;l&agrave;m lạnh tủ bằng c&ocirc;ng nghệ l&agrave;m lạnh dạng v&ograve;m, khi hoạt động c&aacute;c hơi lạnh được lan toả trong c&aacute;c ngăn tủ với dạng h&igrave;nh v&ograve;ng cung. Nhờ đ&oacute; lan tỏa đồng đều khắp c&aacute;c ng&oacute;c ng&aacute;ch v&agrave; gi&uacute;p duy tr&igrave; nhiệt độ v&agrave; độ ẩm ph&ugrave; hợp với thực phẩm để bảo quản l&acirc;u d&agrave;i hơn.</p>\r\n', '17490000', '13990000', 0, 0, 20, 'samsung-inverter-307-lit.jpg'),
(65, 2, 'Tủ lạnh Samsung Inverter 655 lít RS62R5001M9/SV', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tu-lanh/samsung-rs62r5001m9-sv\" target=\"_blank\">Tủ lạnh Samsung Inverter 655 l&iacute;t RS62R5001M9/SV</a>&nbsp;c&oacute; thiết kế kiểu&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh?g=tu-lon-side-by-side\" target=\"_blank\">tủ lạnh side by side</a>&nbsp;đẳng cấp, đi c&ugrave;ng gam m&agrave;u bạc sang trọng, thời thượng, tủ lạnh sẽ l&agrave; điểm nhấn nổi bật, mang lại cho kh&ocirc;ng gian nội thất của gia đ&igrave;nh một vẻ đẹp hiện đại.</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<h3><a href=\"https://www.dienmayxanh.com/tu-lanh?g=tren-550-lit\" target=\"_blank\">Dung t&iacute;ch 655 l&iacute;t</a>&nbsp;d&agrave;nh cho gia đ&igrave;nh đ&ocirc;ng th&agrave;nh vi&ecirc;n</h3>\r\n\r\n<p>Với tổng dung t&iacute;ch sử dụng l&ecirc;n đến 655 l&iacute;t, chiếc&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh-samsung?g=tu-lon-side-by-side\" target=\"_blank\">tủ lạnh Samsung Side by Side</a>&nbsp;n&agrave;y l&agrave; một sự lựa chọn l&yacute; tưởng cho gia đ&igrave;nh c&oacute; kh&ocirc;ng gian sống rộng r&atilde;i, c&ugrave;ng số th&agrave;nh vi&ecirc;n tr&ecirc;n 5 người.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ vậy, c&ocirc;ng nghệ SpaceMax với thiết kế viền si&ecirc;u mỏng của tủ lạnh n&agrave;y gi&uacute;p tăng cường dung t&iacute;ch m&agrave; kh&ocirc;ng thay đổi k&iacute;ch thước ngo&agrave;i. Từ đ&oacute; kh&ocirc;ng gian lưu trữ được rộng r&atilde;i hơn, cho bạn thoải m&aacute;i bảo quản nhiều loại thực phẩm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '22900000', '17390000', 0, 1, 20, 'samsung-rs62r5001m9.jpg'),
(66, 2, 'Tủ lạnh Aqua 90 lít AQR-D99FA(BS)', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tu-lanh/aqua-aqr-d99fa-bs\" target=\"_blank\">Tủ lạnh Aqua 90 l&iacute;t AQR-D99FA(BS)</a>&nbsp;thuộc d&ograve;ng&nbsp;<a href=\"https://www.dienmayxanh.com/tu-lanh?g=mini\" target=\"_blank\">tủ lạnh mini</a>&nbsp;c&oacute; thiết kế nhỏ gọn, ph&ugrave; hợp với những kh&ocirc;ng gian vừa v&agrave; nhỏ. Với kiểu d&aacute;ng nhỏ gọn, chiếc tủ lạnh mini 90 l&iacute;t n&agrave;y sẽ l&agrave; sự lựa chọn l&yacute; tưởng cho những gia đ&igrave;nh nhỏ 1- 2 người hay c&aacute;c bạn sinh vi&ecirc;n.</p>\r\n', '<h3><strong>Chi tiết sản phẩm</strong></h3>\r\n\r\n<h3>L&agrave;m lạnh thực phẩm nhanh ch&oacute;ng với hệ thống l&agrave;m lạnh trực tiếp</h3>\r\n\r\n<p>Hệ thống l&agrave;m lạnh trực tiếp gi&uacute;p l&agrave;m lạnh nhanh ch&oacute;ng nhờ hơi lạnh thổi trực tiếp v&agrave;o thực phẩm. Chiếc tủ lạnh mini sử dụng điện rất tiết kiệm, do đ&oacute; bạn ho&agrave;n to&agrave;n c&oacute; thể y&ecirc;n t&acirc;m sử dụng, kh&ocirc;ng qu&aacute; lo lắng về h&oacute;a đơn tiền điện cuối th&aacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>T&ugrave;y chỉnh nhiệt độ l&agrave;m lạnh dễ d&agrave;ng với n&uacute;t vặn điều chỉnh nhiệt độ</h3>\r\n\r\n<p>Với n&uacute;t vặn điều chỉnh nhiệt độ bạn c&oacute; thể dễ d&agrave;ng điều chỉnh mức nhiệt l&agrave;m lạnh ph&ugrave; hợp với loại thực phẩm m&agrave; bạn lưu trữ. Thực phẩm được bảo quản ở nhiệt độ th&iacute;ch hợp sẽ tươi l&acirc;u hơn, bạn kh&ocirc;ng c&ograve;n lo lắng l&agrave; d&ugrave;ng kh&ocirc;ng kịp trước khi thực phẩm bị hư.</p>\r\n', '3290000', '3190000', 0, 0, 20, 'aqua-aqr.jpg'),
(67, 23, 'Máy lạnh Aqua Inverter 1 HP AQA-RV9QA', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/may-lanh/may-lanh-aqua-inverter-1-hp\" target=\"_blank\">M&aacute;y lạnh Aqua Inverter 1 HP AQA-RV9QA</a>&nbsp;c&oacute; thể h&uacute;t ẩm th&ocirc;ng minh, gi&uacute;p căn ph&ograve;ng lu&ocirc;n kh&ocirc; r&aacute;o v&agrave; mang lại sự thoải m&aacute;i cho người d&ugrave;ng v&agrave;o những ng&agrave;y trời ẩm ướt. Kh&ocirc;ng những thế, m&aacute;y lạnh c&ograve;n c&oacute; thể tự động chẩn đo&aacute;n lỗi, hỗ trợ người d&ugrave;ng khắc phục lỗi nhanh ch&oacute;ng khi gặp phải.</p>\r\n', '<h3><strong>Thiết kế:</strong></h3>\r\n\r\n<p><strong>D&agrave;n lạnh:</strong></p>\r\n\r\n<p>- Thiết kế đơn giản nhưng vẫn to&aacute;t l&ecirc;n vẻ đẹp tinh tế với gam m&agrave;u trắng, ph&ugrave; hợp với mọi kh&ocirc;ng gian lắp đặt. Chất liệu vỏ m&aacute;y bằng nhựa c&oacute; độ bền tốt.</p>\r\n\r\n<p>-&nbsp;<strong>Trang bị m&agrave;n h&igrave;nh hiển thị nhiệt độ</strong>&nbsp;gi&uacute;p người d&ugrave;ng nhanh ch&oacute;ng biết được nhiệt độ của&nbsp;<a href=\"https://www.dienmayxanh.com/may-lanh-aqua\" target=\"_blank\">m&aacute;y lạnh Aqua</a>&nbsp;đang hoạt động, tiện lợi cho việc quan s&aacute;t v&agrave;o ban đ&ecirc;m.</p>\r\n\r\n<p><strong>D&agrave;n n&oacute;ng:</strong></p>\r\n\r\n<p>- Được thiết kế h&igrave;nh hộp chữ nhật nằm ngang với lớp vỏ cứng c&aacute;p, c&oacute; khả năng chịu được tốt khi đặt ở m&ocirc;i trường b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<p>- D&agrave;n tản nhiệt l&agrave;m&nbsp;<strong>bằng nh&ocirc;m, phủ lớp BlueFin</strong>&nbsp;c&oacute; t&aacute;c dụng chống ăn m&ograve;n, giảm thiểu b&aacute;m bụi v&agrave; tăng cường hiệu quả trao đổi nhiệt giữa d&agrave;n n&oacute;ng v&agrave; d&agrave;n lạnh.</p>\r\n\r\n<p>- Ống dẫn gas được l&agrave;m&nbsp;<strong>bằng đồng nguy&ecirc;n chất bền bỉ</strong>, g&oacute;p phần truyền nhiệt nhanh v&agrave; ổn định.</p>\r\n', '9390000', '8190000', 1, 1, 20, 'may-lanh-aqua-inverter-1-hp-1-550x160.jpg'),
(68, 23, 'Máy lạnh Funiki Inverter 1 HP HIC09TMU', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/may-lanh/may-lanh-funiki-inverter-1-hp-hic09tmu\" target=\"_blank\">M&aacute;y lạnh Funiki Inverter 1 HP HIC09TMU</a>&nbsp;c&oacute; kiểu d&aacute;ng tinh tế, hoạt động với c&ocirc;ng suất 1 HP ph&ugrave; hợp với kh&ocirc;ng gian ph&ograve;ng dưới 15m&sup2;. Được trang bị chế độ Eco kết hợp với Inverter giữ lạnh tốt, tiết kiệm điện hiệu quả, chế độ Turbo l&agrave;m lạnh nhanh ch&oacute;ng.</p>\r\n', '<h3><strong>Tổng quan thiết kế</strong></h3>\r\n\r\n<p><strong>D&agrave;n lạnh</strong></p>\r\n\r\n<p>- M&aacute;y lạnh Funiki HIC09TMU c&oacute; kiểu d&aacute;ng h&igrave;nh chữ nhật quen thuộc,&nbsp;<strong>gam m&agrave;u trắng tinh tế</strong>&nbsp;ph&ugrave; hợp với hầu hết kh&ocirc;ng gian nội thất, từ ph&ograve;ng kh&aacute;ch, nh&agrave; bếp cho đến ph&ograve;ng ngủ.&nbsp;</p>\r\n\r\n<p><strong>D&agrave;n n&oacute;ng</strong></p>\r\n\r\n<p>- Kiểu d&aacute;ng khối chữ nhật vu&ocirc;ng vức quen thuộc, thiết kế gọn g&agrave;ng.</p>\r\n\r\n<p>-&nbsp;<strong>Ống dẫn gas bằng đồng</strong>&nbsp;v&agrave;&nbsp;<strong>l&aacute; tản nhiệt bằng nh&ocirc;m được phủ lớp Golden Fin</strong>&nbsp;c&oacute; độ bền cao, khả năng chịu nhiệt tốt, hạn chế ăn m&ograve;n, gi&uacute;p n&acirc;ng cao tuổi thọ của m&aacute;y.&nbsp;</p>\r\n', '8890000', '6490000', 1, 1, 20, 'may-lanh-funiki-inverter-1-hp.jpg'),
(69, 23, 'Máy lạnh Daikin Inverter 1 HP FTKB25WMVMV', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/may-lanh/daikin-inverter-1-hp-ftkb25wmvmv\" target=\"_blank\">M&aacute;y lạnh Daikin Inverter 1 HP FTKB25WMVMV</a>&nbsp;l&agrave; d&ograve;ng m&aacute;y lạnh&nbsp;sở hữu&nbsp;c&ocirc;ng nghệ Inverter​&nbsp;gi&uacute;p tiết kiệm điện năng. Đặc biệt, d&agrave;n n&oacute;ng v&agrave; d&agrave;n lạnh được cải tiến gi&uacute;p m&aacute;y hoạt động &ecirc;m &aacute;i, ổn định, đạt c&ocirc;ng suất tối ưu. Ngo&agrave;i ra, sở hữu phin lọc Enzyme Blue kết hợp phin lọc bụi mịn PM2.5&nbsp;loại bỏ c&aacute;c t&aacute;c nh&acirc;n g&acirc;y m&ugrave;i, g&acirc;y dị ứng, nấm mốc.</p>\r\n', '<h3><strong>Tổng quan thiết kế</strong></h3>\r\n\r\n<p>-&nbsp;<strong><a href=\"https://www.dienmayxanh.com/may-lanh-daikin\" target=\"_blank\">M&aacute;y lạnh Daikin</a>&nbsp;Inverter 1 HP FTKB25WMVMV</strong>&nbsp;sở hữu lớp vỏ ngo&agrave;i bằng nhựa trắng, đi k&egrave;m thiết kế với c&aacute;c cạnh được bo nhẹ, ph&ugrave; hợp đặt ở nhiều kiểu kh&ocirc;ng gian ph&ograve;ng.</p>\r\n\r\n<p>-&nbsp;Ống dẫn gas bằng đồng được cải tiến về cấu tr&uacute;c n&ecirc;n giảm ồn hơn so với d&ograve;ng cũ. L&aacute; tản nhiệt bằng nh&ocirc;m tăng khả năng dẫn nhiệt, độ bền cao.&nbsp;</p>\r\n\r\n<p>- D&agrave;n n&oacute;ng sở hữu c&aacute;c ưu điểm như c&aacute;nh d&agrave;n tản nhiệt được phủ 2 lớp tĩnh điện: lớp nhựa acrylic v&agrave; lớp chống thấm nước, trang bị Super PCB v&agrave; thay đổi h&igrave;nh dạng cũng như th&ecirc;m tấm chắn gi&uacute;p hạn chế sự x&acirc;m nhập của thằn lằn, tăng khả năng chống mưa axit v&agrave; sự ăn m&ograve;n bảo vệ bo mạch.</p>\r\n', '10990000', '9990000', 1, 1, 20, 'daikin-inverter-1.jpg'),
(70, 23, 'Máy lạnh Casper Inverter 1 HP TC-09IS35', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/may-lanh/casper-tc-09is35\" target=\"_blank\">M&aacute;y lạnh Casper Inverter 1 HP TC-09IS35</a>&nbsp;cho khả năng l&agrave;m m&aacute;t hiệu quả với chế độ l&agrave;m lạnh nhanh Turbo, tự động điều chỉnh nhiệt độ với cảm biến nhiệt độ iFeel, sử dụng tiết kiệm điện với c&ocirc;ng nghệ&nbsp;I-saving, tăng tuổi thọ thiết bị với chức năng tự l&agrave;m sạch iClean.</p>\r\n', '<h3><strong>Tổng quan thiết kế</strong></h3>\r\n\r\n<p><strong>D&agrave;n lạnh</strong></p>\r\n\r\n<p>-&nbsp;Casper TC-09IS35 thiết kế bo cạnh viền, t&ocirc;ng m&agrave;u trắng thanh lịch cho tổng thể thiết kế sang trọng, tinh tế, trang tr&iacute; h&agrave;i h&ograve;a với c&aacute;c m&oacute;n đồ nội thất kh&aacute;c trong gian ph&ograve;ng của bạn.&nbsp;</p>\r\n\r\n<p><strong>D&agrave;n n&oacute;ng</strong></p>\r\n\r\n<p>- Thiết kế h&igrave;nh khối đơn giản, chất liệu vỏ ngo&agrave;i c&oacute; độ bền chắc cao, giữ cho c&aacute;c linh kiện b&ecirc;n trong an to&agrave;n trước c&aacute;c điều kiện thời tiết khắc nghiệt, duy tr&igrave; hệ thống hoạt động&nbsp;hiệu quả trong thời gian d&agrave;i.&nbsp;</p>\r\n\r\n<p>-&nbsp;Ống dẫn gas l&agrave;m từ đồng, d&agrave;n tản nhiệt cũng bằng chất liệu đồng mạ v&agrave;ng nhẹ bền, dễ d&agrave;ng lắp đặt.<strong>&nbsp;Bề mặt d&agrave;n tản nhiệt được mạ v&agrave;ng</strong>&nbsp;cho hiệu quả chống ăn m&ograve;n tốt, gia tăng độ bền v&agrave; đảm bảo hoạt động thiết bị tối ưu.&nbsp;</p>\r\n', '7490000', '6990000', 0, 1, 20, 'casper-tc-09is35-1-550x160.jpg'),
(71, 23, 'Máy lạnh Nagakawa Inverter 1 HP NIS-C09R2H10 ', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<h3>Chức năng tự l&agrave;m sạch Auto Clean diệt khuẩn, khử m&ugrave;i hiệu quả</h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/may-lanh/nagakawa-nis-c09r2h10\" target=\"_blank\">M&aacute;y&nbsp;lạnh Nagakawa Inverter 1 HP NIS-C09R2H10</a>&nbsp;c&oacute; chức năng tự l&agrave;m sạch g&oacute;p phần tạo n&ecirc;n bầu kh&ocirc;ng kh&iacute; trong l&agrave;nh bằng c&aacute;ch loại bỏ vi khuẩn, nấm mốc hiệu quả đến 99.9% v&agrave; l&agrave;m kh&ocirc; nước đọng lại ở d&agrave;n tản nhiệt, loại bỏ m&ocirc;i trường sinh s&ocirc;i của mầm bệnh.</p>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/tu-lam-sach-tren-may-lanh-la-gi-1070640\" target=\"_blank\">Chức năng tự l&agrave;m sạch</a>&nbsp;c&ograve;n gi&uacute;p m&aacute;y hoạt động đạt c&ocirc;ng suất tối ưu v&agrave; n&acirc;ng cao tuổi thọ sử dụng m&aacute;y lạnh, tiết kiệm chi ph&iacute; vệ sinh cho gia đ&igrave;nh.</p>\r\n', '<h3><strong>C&ocirc;ng nghệ Inverter si&ecirc;u tiết kiệm điện</strong></h3>\r\n\r\n<p>Với c&ocirc;ng nghệ Inverter kh&ocirc;ng chỉ gi&uacute;p m&aacute;y lạnh hoạt động &ecirc;m &aacute;i, bền bỉ m&agrave; c&ograve;n gi&uacute;p nhiệt độ trong ph&ograve;ng lu&ocirc;n duy tr&igrave; ở mức ổn định, từ đ&oacute; tiết kiệm điện, giảm chi ph&iacute; điện hằng th&aacute;ng đ&aacute;ng kể cho gia đ&igrave;nh bạn.</p>\r\n\r\n<p>Ngo&agrave;i ra, chức năng tiết kiệm năng lượng Economy c&ugrave;ng g&oacute;p phần tiết kiệm năng lượng tối ưu khi được k&iacute;ch hoạt.</p>\r\n\r\n<h3><strong>Lọc sạch kh&ocirc;ng kh&iacute;, bảo vệ sức khỏe với hệ thống l&agrave;m sạch kh&ocirc;ng kh&iacute; 5 lớp</strong></h3>\r\n\r\n<p>Hệ thống l&agrave;m sạch kh&ocirc;ng kh&iacute; tr&ecirc;n&nbsp;<a href=\"https://www.dienmayxanh.com/may-lanh-nagakawa\" target=\"_blank\">m&aacute;y lạnh Nagakawa</a>&nbsp;với m&agrave;ng lọc 5 lớp lọc c&oacute; t&aacute;c dụng l&agrave;m sạch kh&ocirc;ng kh&iacute; v&agrave; loại bỏ c&aacute;c t&aacute;c nh&acirc;n g&acirc;y ảnh hưởng xấu đến sức khỏe con người như vi khuẩn, bụi bẩn, nấm mốc,... một c&aacute;ch tối ưu.</p>\r\n', '9390000', '8990000', 0, 0, 20, 'nagakawa-nis-c09r2h10-11-550x160.jpg'),
(72, 23, 'Máy lạnh Gree Inverter 1 HP GWC09PB-K3D0P4', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<h3><strong>Loại bỏ vi khuẩn, ngăn ngừa nấm mốc với t&iacute;nh năng tự l&agrave;m sạch G-Clean</strong></h3>\r\n\r\n<p>Khi k&iacute;ch hoạt t&iacute;nh năng n&agrave;y, m&aacute;y lạnh sẽ tự động l&agrave;m sạch d&agrave;n lạnh một c&aacute;ch dễ d&agrave;ng th&ocirc;ng qua 4 bước: Ngưng tụ, Đ&oacute;ng băng, R&atilde; đ&ocirc;ng v&agrave; Sấy kh&ocirc;, gi&uacute;p loại bỏ hiệu quả vi khuẩn v&agrave; ngăn ngừa nấm mốc tr&ecirc;n d&agrave;n lạnh.</p>\r\n\r\n<h3><strong>Điều chỉnh linh hoạt nhiệt độ nhờ&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/cac-cam-bien-thong-minh-tren-may-lanh-1080334#i-feel-gree\" target=\"_blank\">cảm biến I Feel&nbsp;</a></strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/may-lanh/gree-gwc09pb-k3d0p4\" target=\"_blank\">M&aacute;y lạnh Gree Inverter 1 HP GWC09PB-K3D0P4</a>&nbsp;được trang bị hệ cảm biến I Feel, c&oacute; khả năng cảm nhận sự thay đổi nhiệt độ của m&ocirc;i trường, từ đ&oacute; truyền t&iacute;n hiệu đến d&agrave;n lạnh để tự động điều chỉnh nhiệt độ trong ph&ograve;ng, gi&uacute;p cho người d&ugrave;ng lu&ocirc;n cảm thấy m&aacute;t lạnh.&nbsp;</p>\r\n', '', '9790000', '8190000', 0, 1, 0, 'gree-gwc09pb-k3d0p4-550x160.jpg'),
(73, 4, 'Điện thoại Samsung Galaxy A24 6GB', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/dien-thoai/samsung-galaxy-a24\" target=\"_blank\">Samsung Galaxy A24 6GB</a>&nbsp;- l&agrave; thế hệ điện thoại thuộc d&ograve;ng Galaxy vừa được nh&agrave; Samsung giới thiệu đến thị trường Việt Nam v&agrave;o th&aacute;ng 04/2023. M&aacute;y sở hữu cụm camera c&oacute; độ ph&acirc;n giải ch&iacute;nh 50 MP, chip tốt c&ugrave;ng m&agrave;n h&igrave;nh Super AMOLED chất lượng.</p>\r\n', '<h3><strong>Thiết kế vu&ocirc;ng vắn hợp xu hướng</strong></h3>\r\n\r\n<p>Về thiết kế, chiếc m&aacute;y n&agrave;y được lấy cảm hứng từ một v&agrave;i d&ograve;ng sản phẩm cao cấp của Samsung như Galaxy S23 series, với tạo h&igrave;nh phẳng ở phần bộ khung v&agrave; mặt lưng gi&uacute;p cho tổng thể chiếc m&aacute;y trở n&ecirc;n sang trọng v&agrave; hiện đại hơn rất nhiều.</p>\r\n\r\n<p>Vỏ ngo&agrave;i của m&aacute;y được chế tạo chủ yếu từ nhựa, đ&oacute;ng vai tr&ograve; quan trọng trong việc giảm thiểu khối lượng v&agrave; tạo cảm gi&aacute;c nhẹ nh&agrave;ng khi cầm nắm sản phẩm.</p>\r\n\r\n<h3><strong>Trang bị m&agrave;n h&igrave;nh Super AMOLED cao cấp</strong></h3>\r\n\r\n<p>Samsung đ&atilde; t&iacute;ch hợp c&ocirc;ng nghệ m&agrave;n h&igrave;nh Super AMOLED tr&ecirc;n chiếc điện thoại n&agrave;y để mang đến cho người d&ugrave;ng những ưu điểm vượt trội. M&agrave;n h&igrave;nh gi&uacute;p hiển thị h&igrave;nh ảnh sống động, tối ưu h&oacute;a ti&ecirc;u thụ điện v&agrave; giảm thiểu lượng &aacute;nh s&aacute;ng xanh để bảo vệ mắt.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh của m&aacute;y được trang bị độ ph&acirc;n giải Full HD+ (1080 x 2340 Pixels) v&agrave; c&oacute; k&iacute;ch thước vừa phải khoảng 6.5 inch. Điều n&agrave;y gi&uacute;p tăng mật độ điểm ảnh để mọi nội dung hiển thị trở n&ecirc;n ch&acirc;n thực v&agrave; sắc n&eacute;t hơn.</p>\r\n', '6490000', '6090000', 1, 1, 20, 'samsung-galaxy-a24.jpg'),
(74, 4, 'Điện thoại OPPO Find N2 Flip 5G', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/dien-thoai/oppo-find-n2-flip\" target=\"_blank\">OPPO Find N2 Flip</a>&nbsp;l&agrave; mẫu điện thoại gập đầu ti&ecirc;n được nh&agrave; OPPO ch&iacute;nh thức giới thiệu tại Việt Nam, sản phẩm mang đến một thiết kế độc đ&aacute;o cho người d&ugrave;ng cũng như hiệu năng mạnh mẽ từ con chip Dimensity 9000+ v&agrave; m&agrave;n h&igrave;nh phụ si&ecirc;u lớn được t&iacute;ch hợp k&egrave;m.</p>\r\n', '<h3><strong>Thiết kế gấp gọn đầy mới lạ</strong></h3>\r\n\r\n<p>Thiết kế của Find N2 Flip mang lại sự ấn tượng bởi cơ chế gập độc đ&aacute;o v&agrave; tỷ lệ khung h&igrave;nh rộng kết hợp viền mỏng, những điều n&agrave;y tạo n&ecirc;n một c&aacute;i nh&igrave;n đẹp mắt đầy sang trọng.&nbsp;</p>\r\n\r\n<p>Với việc gấp gọn một c&aacute;ch dễ d&agrave;ng, điều n&agrave;y gi&uacute;p ta c&oacute; thể biến chiếc m&aacute;y trở n&ecirc;n nhỏ nhắn hơn để c&oacute; thể mang đến bất kỳ nơi đ&acirc;u, được xem l&agrave; một điểm hết sức th&uacute; vị v&agrave; tiện &iacute;ch, hứa hẹn sẽ trở th&agrave;nh kiểu d&aacute;ng dẫn đầu trong tương lai.</p>\r\n\r\n<p>Một điểm cải tiến đ&aacute;ng ch&uacute; &yacute; ở phần thiết kế của mẫu m&aacute;y n&agrave;y l&agrave; về phần gập của bản lề, Find N2 Flip được chế tạo tốt n&ecirc;n khoảng hở của m&agrave;n h&igrave;nh khi gập l&agrave; kh&ocirc;ng qu&aacute; lớn, gi&uacute;p hạn chế c&aacute;c dị vật nhỏ li ti bị rơi v&agrave;o phần m&agrave;n h&igrave;nh</p>\r\n', '19990000', '18990000', 1, 1, 20, 'oppo-find-n2-flip-purple-thumb.jpg'),
(75, 4, 'Điện thoại iPhone 14 Pro Max 128GB', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p>Điểm nhấn của sự kiện ra mắt sản phẩm th&aacute;ng 9/2022 của Apple ch&iacute;nh l&agrave; si&ecirc;u phẩm&nbsp;<a href=\"https://www.dienmayxanh.com/dien-thoai/iphone-14-pro-max\" target=\"_blank\">iPhone 14 Pro Max 128GB</a>&nbsp;với thiết kế mặt trước đổi mới, camera n&acirc;ng cấp mạnh mẽ v&agrave; hiệu năng b&ugrave;ng nổ từ chip A16 Bionic, đ&aacute;p ứng mọi nhu cầu của người d&ugrave;ng.</p>\r\n', '<h3><strong>Thiết kế m&agrave;n h&igrave;nh đục lỗ độc đ&aacute;o</strong></h3>\r\n\r\n<p>M&agrave;n h&igrave;nh 6.7 inch độ ph&acirc;n giải Super Retina XDR cho khả năng hiển thị si&ecirc;u sắc n&eacute;t, m&agrave;u sắc sống động. Kết hợp c&ugrave;ng c&ocirc;ng nghệ ProMotion tự động thay đổi tốc độ l&agrave;m tươi m&agrave;n h&igrave;nh từ 1 Hz - 120 Hz, để mọi khung h&igrave;nh được chuyển động mượt m&agrave; nhất trong khi vẫn tối ưu tiết kiệm được thời lượng pin.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh c&oacute; độ s&aacute;ng si&ecirc;u cao l&ecirc;n đến 2000 nits, vẫn c&oacute; thể hiển thị một c&aacute;ch r&otilde; r&agrave;ng nhất trong m&ocirc;i trường c&oacute; &aacute;nh s&aacute;ng cao, thậm ch&iacute; dưới &aacute;nh nắng mặt trời. Đặc biệt l&agrave; t&iacute;nh năng Always On Display&nbsp;để người d&ugrave;ng c&oacute; thể xem nhanh th&ocirc;ng tin tr&ecirc;n m&agrave;n h&igrave;nh m&agrave; kh&ocirc;ng cần phải đ&aacute;nh thức thiết bị.</p>\r\n\r\n<h3><strong>Camera n&acirc;ng cấp độ ph&acirc;n giải</strong></h3>\r\n\r\n<p>Một trong những cải tiến lớn nhất m&agrave; Apple thực hiện tr&ecirc;n&nbsp;<a href=\"https://www.dienmayxanh.com/dien-thoai-apple-iphone\" target=\"_blank\">điện thoại iPhone</a>&nbsp;năm n&agrave;y ch&iacute;nh l&agrave; n&acirc;ng cấp camera ch&iacute;nh l&ecirc;n 48 MP, sử dụng c&ocirc;ng nghệ gộp 4 px th&agrave;nh 1 px để c&oacute; được h&igrave;nh ảnh với chất lượng tốt hơn.</p>\r\n', '29990000', '26990000', 1, 0, 20, 'iphone-14-pro-max-den-thumb.jpg');
INSERT INTO `tbl_sanpham` (`sanpham_id`, `category_id`, `sanpham_name`, `sanpham_chitiet`, `sanpham_mota`, `sanpham_gia`, `sanpham_giakhuyenmai`, `sanpham_active`, `sanpham_hot`, `sanpham_soluong`, `sanpham_image`) VALUES
(76, 4, 'Điện thoại iPhone 14 Pro 128GB', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p>Mới đ&acirc;y (09/2022), Apple đ&atilde; tung ra thị trường mẫu điện thoại iPhone 14 series, trong đ&oacute; c&oacute; một c&aacute;i t&ecirc;n nhận được nhiều sự quan t&acirc;m của giới c&ocirc;ng nghệ l&agrave; iPhone 14 Pro, sở hữu hiệu năng mạnh mẽ, m&agrave;n h&igrave;nh hiển thị h&igrave;nh ảnh ch&acirc;n thực v&agrave; cụm camera cho khả năng chụp ảnh đẹp mắt.</p>\r\n', '<h3><strong>Thiết kế hiện đại, đẳng cấp &ldquo;chuẩn gu&rdquo; iFan</strong></h3>\r\n\r\n<p>So với phi&ecirc;n bản tiền nhiệm, thiết kế b&ecirc;n ngo&agrave;i chiếc&nbsp;<a href=\"https://www.dienmayxanh.com/dtdd/iphone-14-pro\" target=\"_blank\">iPhone 14 Pro</a>&nbsp;n&agrave;y kh&ocirc;ng kh&aacute;c biệt qu&aacute; nhiều. Khung viền phẳng sử dụng chất liệu th&eacute;p kh&ocirc;ng gỉ cứng c&aacute;p vừa thể hiện sự sang trọng cho thiết bị vừa gi&uacute;p bảo vệ điện thoại hiệu quả.</p>\r\n\r\n<p>Về m&agrave;u sắc: iPhone 14 Pro vẫn bao gồm nhiều phi&ecirc;n bản m&agrave;u sắc kh&aacute;c nhau, lần n&agrave;y Apple bổ sung m&agrave;u t&iacute;m lavender đẹp mắt, thời thượng.</p>\r\n\r\n<p>Một điều đặc biệt, đối với thế hệ iPhone 14 lần n&agrave;y, Apple đ&atilde; ch&iacute;nh thức bỏ phần tai thỏ quen thuộc v&agrave; thay v&agrave;o đ&oacute; l&agrave; thiết kế h&igrave;nh vi&ecirc;n thuốc bao bọc camera trước.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh OLED cho m&agrave;u sắc sống động, hiển thị vượt trội</strong></h3>\r\n\r\n<p>Sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh OLED hiện đại gi&uacute;p c&aacute;c m&agrave;u sắc hiển thị sống động, cải thiện độ tương phản, thể hiện r&otilde; r&agrave;ng từng chi tiết, từ đ&oacute; cho chất lượng h&igrave;nh ảnh tốt hơn.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh hiển thị r&otilde; n&eacute;t ngay cả khi trong điều kiện &aacute;nh s&aacute;ng cường độ cao như mặt trời nhờ độ s&aacute;ng m&agrave;n h&igrave;nh l&ecirc;n tới 2000 nits.</p>\r\n\r\n<p>Tần số qu&eacute;t 120 Hz cho c&aacute;c thao t&aacute;c vuốt, chạm tr&ecirc;n m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.dienmayxanh.com/dtdd\" target=\"_blank\">điện thoại</a>&nbsp;diễn ra mượt m&agrave;.&nbsp;</p>\r\n', '27990000', '24790000', 0, 1, 20, 'iphone-14-pro-vang-thumb-600x600.jpg'),
(77, 4, 'Điện thoại Samsung Galaxy S21 FE 5G (8GB/128GB)', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/samsung\" target=\"_blank\">Samsung</a>&nbsp;mang đến cho người d&ugrave;ng phi&ecirc;n bản&nbsp;<a href=\"https://www.dienmayxanh.com/dien-thoai/samsung-galaxy-s21-fe\" target=\"_blank\">điện thoại Samsung Galaxy S21 FE 5G (8GB/128GB)</a>&nbsp;mới c&oacute; sức h&uacute;t hấp dẫn với ngoại h&igrave;nh tinh tế, nịnh mắt, một cấu h&igrave;nh mạnh mẽ từ chip Exynos 2100 v&agrave; ấn tượng ở khả năng quay chụp từ hệ thống camera th&ocirc;ng minh, xịn s&ograve;.</p>\r\n', '<h3><strong>Cảm nhận chạm vuốt cực mượt, khung h&igrave;nh rực rỡ sắc n&eacute;t</strong></h3>\r\n\r\n<p>Samsung Galaxy S21 FE 5G sở hữu tấm nền Dynamic AMOLED 2X với dải m&agrave;u cực rộng v&agrave; độ tương phản cao, độ ph&acirc;n giải chuẩn Full HD+ thể hiện từng chi tiết v&agrave; m&agrave;u sắc chuẩn x&aacute;c, sắc n&eacute;t đầy ch&acirc;n thực. Độ s&aacute;ng m&agrave;n h&igrave;nh tối đa 1200 nits n&ecirc;n khi sử dụng ngo&agrave;i trời vẫn mang đến chất lượng hiển thị tốt, kh&ocirc;ng bị l&oacute;a mờ.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh 6.4 inch được phủ lớp k&iacute;nh cường lực Gorilla Glass Victus chống nứt vỡ v&agrave; trầy xước tốt cho m&agrave;n h&igrave;nh hiển thị c&oacute; độ trong suốt cao, đồng thời bền bỉ trong qu&aacute; tr&igrave;nh sử dụng. Camera nốt ruồi v&agrave; c&aacute;c cạnh viền mỏng cho kh&ocirc;ng gian trải nghiệm m&agrave;n h&igrave;nh tối ưu hơn. Chuẩn kh&aacute;ng nước v&agrave; chống bụi IP68 cũng bảo vệ tốt&nbsp;<a href=\"https://www.dienmayxanh.com/dien-thoai\" target=\"_blank\">smartphone</a>&nbsp;khi v&ocirc; t&igrave;nh tiếp x&uacute;c với nước.</p>\r\n\r\n<p>Đặc biệt, trải nghiệm cảm gi&aacute;c chạm, lướt, cuộn m&agrave;n h&igrave;nh v&agrave; chuyển cảnh cực mượt nhờ tần số qu&eacute;t l&ecirc;n đến 120 Hz, thao t&aacute;c kh&ocirc;ng độ trễ, tận dụng từng cơ hội l&agrave;m chủ trong c&aacute;c trận đấu game gay cấn.</p>\r\n', '16990000', '11990000', 0, 0, 20, 'Samsung-Galaxy-S21-FE-den-600x600.jpg'),
(78, 4, 'Điện thoại Vivo v27e 8GB/256GB', '<p><a href=\"https://www.dienmayxanh.com/dien-thoai/vivo-v27e\" target=\"_blank\">Vivo V27e</a>&nbsp;một sản phẩm mới nhất thuộc d&ograve;ng V27 series của Vivo. Với mức gi&aacute; hợp l&yacute;, thiết bị n&agrave;y c&oacute; nhiều t&iacute;nh năng đ&aacute;ng ch&uacute; &yacute; như được trang bị cụm camera chất lượng cao với khả năng chống rung OIS. Ngo&agrave;i ra, thiết kế của Vivo V27e cũng rất đẹp mắt v&agrave; c&oacute; dung lượng pin l&ecirc;n đến 4600 mAh.</p>\r\n', '<h3><strong>Thiết kế tối giản v&agrave; sang trọng</strong></h3>\r\n\r\n<p>Vivo V27e đi k&egrave;m với những t&ocirc;ng m&agrave;u hiện đại v&agrave; tươi trẻ c&oacute; thể kể đến l&agrave; m&agrave;u t&iacute;m v&agrave; đen. Những m&agrave;u sắc n&agrave;y dễ d&agrave;ng cho bạn phối hợp với nhiều loại trang phục kh&aacute;c nhau gi&uacute;p bạn trở n&ecirc;n nổi bật hơn ở những chỗ đ&ocirc;ng người.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, phần mặt lưng Vivo V27e được l&agrave;m nh&aacute;m mờ, c&oacute; độ phản chiếu &aacute;nh s&aacute;ng nhất định, do đ&oacute; khi nh&igrave;n điện thoại dưới c&aacute;c g&oacute;c nh&igrave;n kh&aacute;c nhau c&oacute; thể sẽ thấy được một ch&uacute;t lấp l&aacute;nh qua mặt lưng.</p>\r\n\r\n<p>Về camera sau, Vivo V27e được trang bị cụm camera xếp thẳng h&agrave;ng nằm gọn g&agrave;ng trong m&ocirc;-đun h&igrave;nh chữ nhật tr&ocirc;ng rất tinh tế v&agrave; đẹp mắt, k&egrave;m theo đ&oacute; l&agrave; đ&egrave;n LED với dạng v&ograve;ng tr&ograve;n h&agrave;o quang m&agrave; h&atilde;ng gọi l&agrave; Aura Light v&ocirc; c&ugrave;ng lạ mắt.</p>\r\n\r\n<p>Vivo V27e cho cảm gi&aacute;c cầm nắm tốt với khối lượng 185 g v&agrave; độ d&agrave;y l&agrave; 7.7 mm (phi&ecirc;n bản m&agrave;u đen) cho bạn thoải m&aacute;i sử dụng cả ng&agrave;y m&agrave; cũng kh&ocirc;ng hề cảm thấy mỏi tay. Với khung viền được l&agrave;m phẳng, v&igrave; thế chiếc&nbsp;<a href=\"https://www.dienmayxanh.com/dien-thoai-vivo-v\" target=\"_blank\">điện thoại Vivo d&ograve;ng V</a>&nbsp;n&agrave;y tr&ocirc;ng kh&aacute; bắt mắt v&agrave; sang trọng hơn.</p>\r\n', '8990000', '8990000', 0, 0, 20, 'vivo-v27e-den-thumb-600x600.jpg'),
(79, 3, 'Máy giặt Samsung Inverter 10kg', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<h3>Giặt sạch hơn, bảo vệ quần &aacute;o như mới với c&ocirc;ng nghệ giặt bong b&oacute;ng&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/cong-nghe-giat-tren-may-giat-samsung-586471#eco-bubble\" target=\"_blank\">Eco Bubble</a></h3>\r\n\r\n<p>C&ocirc;ng nghệ giặt n&agrave;y tạo n&ecirc;n những bong b&oacute;ng x&agrave; ph&ograve;ng si&ecirc;u mịn dễ d&agrave;ng th&acirc;m nhập v&agrave;o từng sợi vải nhanh gấp<strong>&nbsp;40 lần</strong>&nbsp;giặt th&ocirc;ng thường, đ&aacute;nh bật vết bẩn nhanh hơn<strong>&nbsp;24%&nbsp;</strong>so với trước đ&acirc;y, loại bỏ cặn b&aacute;m v&agrave; bảo vệ &aacute;o quần bền m&agrave;u hơn&nbsp;<strong>45%</strong>.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<h3>Diệt khuẩn, ngừa t&aacute;c nh&acirc;n dị ứng hiệu quả th&ocirc;ng qua c&ocirc;ng nghệ giặt hơi nước Hygiene Steam</h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/may-giat-samsung\" target=\"_blank\">M&aacute;y giặt Samsung&nbsp;</a>sẽ sử dụng hơi nước c&oacute; nhiệt độ cao thổi trực tiếp v&agrave;o quần &aacute;o, hơi n&oacute;ng sẽ&nbsp;<strong>thẩm thấu v&agrave;o từng sợi vải</strong>&nbsp;gi&uacute;p<strong>&nbsp;loại bỏ vi khuẩn</strong>&nbsp;v&agrave; c&aacute;c&nbsp;<strong>t&aacute;c nh&acirc;n g&acirc;y dị ứng với hiệu suất l&ecirc;n đến 99.9%</strong>&nbsp;b&aacute;m trong quần &aacute;o một c&aacute;ch hiệu quả, bảo vệ sức khỏe của bạn tốt hơn.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ loại bỏ vi khuẩn tốt hơn c&aacute;ch giặt th&ocirc;ng thường m&agrave; c&ocirc;ng nghệ giặt hơi nước Hygiene Steam c&ograve;n giữ cho vải lu&ocirc;n mềm mại v&agrave; ngay ngắn.</p>\r\n', '15590000', '11390000', 1, 1, 20, 'samsung-inverter-10kg.jpg'),
(80, 3, 'Máy giặt Aqua Inverter 9 kg AQD-A902G S', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p>M&aacute;y giặt Aqua Inverter 9 kg AQD-A902G S&nbsp;c&oacute; thiết kế mỏng gọn, tiết kiệm diện t&iacute;ch cho kh&ocirc;ng gian lắp đặt, c&ocirc;ng nghệ giặt hơi nước được t&iacute;ch hợp gi&uacute;p diệt khuẩn quần &aacute;o, ph&ugrave; hợp với những người c&oacute; l&agrave;n da nhạy cảm dễ bị mẩn đỏ do c&aacute;c t&aacute;c nh&acirc;n dị ứng.</p>\r\n', '<h3><strong>Tổng quan thiết kế</strong></h3>\r\n\r\n<p>- Kiểu d&aacute;ng:&nbsp;<a href=\"https://www.dienmayxanh.com/may-giat?g=cua-truoc\" target=\"_blank\"><strong>m&aacute;y giặt cửa trước - lồng ngang</strong></a>. Thiết kế mỏng gọn v&agrave; tinh tế với gam m&agrave;u bạc sang trọng, tiết kiệm diện t&iacute;ch, tăng th&ecirc;m kh&ocirc;ng gian sống cho gia đ&igrave;nh.&nbsp;</p>\r\n\r\n<p>- Bảng điều khiển&nbsp;<strong>tiếng Việt</strong>&nbsp;dễ sử dụng kết hợp&nbsp;<strong>n&uacute;m xoay</strong>&nbsp;lựa chọn chương tr&igrave;nh giặt. Ngo&agrave;i ra, m&aacute;y giặt c&ograve;n được trang bị&nbsp;<strong>m&agrave;n h&igrave;nh LED</strong>&nbsp;hiển thị r&otilde; r&agrave;ng c&aacute;c th&ocirc;ng số giặt giũ, gi&uacute;p người d&ugrave;ng dễ d&agrave;ng theo d&otilde;i.</p>\r\n\r\n<p>- Nắp m&aacute;y:&nbsp;<strong>chất liệu nhựa ABS v&agrave; k&iacute;nh&nbsp;</strong>hạn chế nguy cơ bỏng r&aacute;t khi v&ocirc; t&igrave;nh chạm tay v&agrave;o nắp m&aacute;y giặt ở c&aacute;c chế độ giặt hơi nước.&nbsp;</p>\r\n\r\n<p>- Lồng giặt k&iacute;ch thước lớn&nbsp;<strong>525 mm tăng kh&ocirc;ng gian rơi thả rộng, quần &aacute;o giảm xoắn rối</strong>, c&ugrave;ng<strong>&nbsp;thiết kế dạng h&igrave;nh gối (Pillow)&nbsp;</strong>với c&aacute;c lỗ tho&aacute;t nước nhỏ&nbsp;gi&uacute;p c&aacute;c tia nước phun ra mạnh hơn, từ đ&oacute; hạn chế tưa sờn, vướng chỉ, n&acirc;ng cao hiệu quả giặt giũ.&nbsp;</p>\r\n', '11390000', '5990000', 1, 0, 20, 'aqua-inverter-9-kg-aqd.jpg'),
(81, 3, 'Máy giặt LG Inverter 8.5 kg FV1408S4W', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<h3><strong>Thiết kế tinh tế v&agrave; hiện&nbsp;đại</strong></h3>\r\n\r\n<p>Với&nbsp;<a href=\"https://www.dienmayxanh.com/may-giat?g=tu-8-9-kg-3-5-nguoi\" target=\"_blank\">khối lượng giặt đến 8.5 Kg</a>&nbsp;nhưng tổng thể&nbsp;<a href=\"https://www.dienmayxanh.com/may-giat\" target=\"_blank\">m&aacute;y giặt</a>&nbsp;vẫn v&ocirc; c&ugrave;ng nhỏ gọn. C&ugrave;ng với m&agrave;u trắng thanh tho&aacute;t, sẽ gi&uacute;p ng&ocirc;i nh&agrave; của bạn tr&ocirc;ng hiện đại hơn.</p>\r\n\r\n<p>Lồng giặt của LG Inverter 8.5 kg FV1408S4W c&ograve;n c&oacute; khả năng chống b&aacute;m vi khuẩn đến 99.9% với chất liệu từ th&eacute;p kh&ocirc;ng gỉ, nắp m&aacute;y giặt từ k&iacute;nh chịu lực gi&uacute;p đảm bảo độ bền tối đa cho thiết bị.</p>\r\n', '<h3><strong>14 chương tr&igrave;nh giặt đa dạng gi&uacute;p đ&aacute;p ứng tốt c&aacute;c nhu cầu giặt giũ trong gia đ&igrave;nh</strong></h3>\r\n\r\n<p>Bảng điều khiển được thiết kế dạng n&uacute;m xoay tr&ograve;n nhẹ nh&agrave;ng, gi&uacute;p dễ d&agrave;ng lựa chọn chương tr&igrave;nh giặt mong muốn. Trong đ&oacute;, 8 chương tr&igrave;nh giặt hay d&ugrave;ng c&oacute; thể kể đến như giặt nhanh 30 ph&uacute;t, giặt ngừa dị ứng, giặt hơi nước, giặt đồ len, giặt &ecirc;m, giặt chăn ga, giặt nhẹ, vải b&ocirc;ng (<a href=\"https://www.dienmayxanh.com/may-giat/may-giat-lg-inverter-85-kg-fv1408s4w#top-tskt\" target=\"_blank\">xem chi tiết tại bảng th&ocirc;ng số kỹ thuật</a>).</p>\r\n\r\n<p>Nổi bật l&agrave; chương tr&igrave;nh giặt nhanh 30 ph&uacute;t, chức năng n&agrave;y th&iacute;ch hợp d&ugrave;ng cho những loại đồ &iacute;t bẩn, gi&uacute;p tiết kiệm nước giặt hay khi bạn c&oacute; nhu cầu sử dụng lại bộ đồ đ&oacute; trong khoảng thời gian ngắn.</p>\r\n', '12190000', '10090000', 1, 1, 20, 'may-giat-lg-inverter-85-kg.jpg'),
(82, 1, 'Laptop Acer Aspire 7 Gaming A715 42G R05G', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/laptop/acer-aspire-7-gaming-a715-42g-r05g-r5-nhqaysv007\" target=\"_blank\">Laptop Acer Aspire 7 Gaming A715 42G R05G R5 (NH.QAYSV.007)</a>&nbsp;c&oacute; kiểu d&aacute;ng hiện đại c&ugrave;ng hiệu năng mạnh mẽ c&oacute; thể đ&aacute;p ứng mọi nhu cầu sử dụng từ văn ph&ograve;ng đến đồ hoạ của c&aacute;c bạn sinh vi&ecirc;n v&agrave; nh&acirc;n vi&ecirc;n văn ph&ograve;ng trong cả một ng&agrave;y.</p>\r\n', '<p>&bull;&nbsp;<a href=\"https://www.dienmayxanh.com/laptop-ryzen-5\" target=\"_blank\">Laptop&nbsp;AMD Ryzen 5</a><strong>&nbsp;5500U&nbsp;</strong>v&agrave; card đồ họa rời&nbsp;<strong>NVIDIA GeForce GTX 1650 4 GB</strong>&nbsp;cho hiệu năng mạnh mẽ gi&uacute;p xử l&yacute; tốt c&aacute;c t&aacute;c vụ văn ph&ograve;ng, đồ họa 2D, render video ngắn,...&nbsp;</p>\r\n\r\n<p>&bull; M&agrave;n h&igrave;nh&nbsp;<strong>15.6 inch</strong>&nbsp;c&ugrave;ng c&ocirc;ng nghệ&nbsp;<strong>Acer ComfyView</strong>&nbsp;cho h&igrave;nh ảnh sắc n&eacute;t, m&agrave;u sắc ch&acirc;n thật. C&ocirc;ng nghệ chống ch&oacute;i&nbsp;<strong>Anti Glare&nbsp;</strong>gi&uacute;p&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=laptop-gaming\" target=\"_blank\">laptop gaming</a>&nbsp;hiển thị h&igrave;nh ảnh r&otilde; r&agrave;ng khi d&ugrave;ng ở nơi &aacute;nh s&aacute;ng mạnh kết hợp c&ugrave;ng tấm nền&nbsp;<strong>IPS&nbsp;</strong>cho g&oacute;c nh&igrave;n rộng.</p>\r\n\r\n<p>&bull; Tần số qu&eacute;t&nbsp;<strong>144 Hz</strong>&nbsp;gi&uacute;p&nbsp;h&igrave;nh ảnh chuyển động mượt m&agrave; hơn, giảm tối đa t&igrave;nh trạng giật, x&eacute; h&igrave;nh.</p>\r\n\r\n<p>&bull;&nbsp;<strong>RAM 8 GB</strong>&nbsp;c&ugrave;ng&nbsp;<strong>SSD 512 GB</strong>&nbsp;gi&uacute;p chạy đa nhiệm trơn tru, kh&ocirc;ng gian lưu trữ đủ d&ugrave;ng v&agrave; n&acirc;ng cao tốc độ phản hồi cho m&aacute;y.</p>\r\n\r\n<p>&bull;&nbsp;<a href=\"https://www.dienmayxanh.com/laptop-acer-aspire\">Laptop Acer Aspire</a>&nbsp;c&oacute; phong c&aacute;ch thiết kế hiện đại với lớp&nbsp;<strong>vỏ nhựa</strong>&nbsp;m&agrave;u đen mạnh mẽ, khối lượng m&aacute;y&nbsp;<strong>2.1 kg</strong>&nbsp;v&agrave; độ d&agrave;y&nbsp;<strong>22.9 mm</strong>&nbsp;kh&ocirc;ng qu&aacute; cồng kềnh để người d&ugrave;ng c&oacute; thể mang theo đi học, đi l&agrave;m hay c&ocirc;ng t&aacute;c.</p>\r\n\r\n<p>&bull;&nbsp;<a href=\"https://www.dienmayxanh.com/laptop-acer\">Laptop Acer</a>&nbsp;c&oacute; bản lề mở<strong>&nbsp;180 độ&nbsp;</strong>hỗ trợ người d&ugrave;ng chia sẻ m&agrave;n h&igrave;nh của m&igrave;nh với mọi người xung quanh một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p>&bull; Trang bị<strong>&nbsp;đ&egrave;n b&agrave;n ph&iacute;m&nbsp;</strong>đơn sắc<strong>&nbsp;</strong>gi&uacute;p người d&ugrave;ng c&oacute; thể g&otilde; ph&iacute;m ch&iacute;nh x&aacute;c hơn khi d&ugrave;ng ở nơi &aacute;nh s&aacute;ng yếu. Laptop c&ograve;n t&iacute;ch hợp đa dạng cổng kết nối như 2 cổng USB 3.2, HDMI, LAN (RJ45), USB 2.0, USB Type-C.</p>\r\n\r\n<p>&bull;&nbsp;<a href=\"https://www.dienmayxanh.com/laptop\">Laptop</a>&nbsp;sử dụng c&ocirc;ng nghệ&nbsp;<strong>Acer TrueHarmony&nbsp;</strong>mang đến chất &acirc;m trong trẻo, sống động, cho trải nghiệm giải tr&iacute; ch&acirc;n thật v&agrave; hấp dẫn.</p>\r\n', '20490000', '15990000', 1, 1, 20, 'acer-aspire-7-gaming-a715.jpg'),
(83, 1, 'Laptop Lenovo Ideapad Gaming 3 15IHU6 i5', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/laptop/lenovo-ideapad-gaming-3-15ihu6-i5-82k10178vn\" target=\"_blank\">Laptop Lenovo Ideapad Gaming 3 15IHU6 i5&nbsp;(82K101F3VN)</a>&nbsp;g&acirc;y ấn tượng với ngoại h&igrave;nh tối giản nhưng kh&ocirc;ng k&eacute;m phần cuốn h&uacute;t, cấu h&igrave;nh mạnh mẽ nhờ CPU Intel Core i5 thế hệ 11 v&agrave; card đồ họa&nbsp;NVIDIA GeForce GTX 1650 gi&uacute;p bạn chinh phục mọi đấu trường ảo.</p>\r\n', '', '20790000', '15990000', 1, 1, 20, 'lenovo-ideapad-gaming-3.jpg'),
(84, 1, 'Laptop HP Gaming VICTUS 15 fa0111TX i5', '<h3><strong>Th&ocirc;ng tin sản phẩm</strong></h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/laptop/hp-victus-15-fa0111tx-i5-7c0r4pa\" target=\"_blank\">Laptop HP VICTUS 15 fa0111TX i5 12500H (7C0R4PA)</a>&nbsp;l&agrave; mẫu&nbsp;<a href=\"https://www.dienmayxanh.com/laptop?g=laptop-gaming\" target=\"_blank\">laptop gaming</a>&nbsp;sở hữu bộ vi xử l&yacute; Intel Core i5 12500H mạnh mẽ, card đồ họa NVIDIA GeForce RTX 3050Ti 4 GB v&agrave; m&agrave;n h&igrave;nh si&ecirc;u mượt m&agrave; 144 Hz, hứa hẹn đem đến cho bạn trải nghiệm l&agrave;m việc v&agrave; giải tr&iacute; tốt nhất.</p>\r\n', '<p>&bull;&nbsp;Bộ vi xử l&yacute;&nbsp;<strong>Intel Core i5 12500H</strong>&nbsp;v&agrave; card đồ họa&nbsp;<strong>NVIDIA GeForce RTX 3050Ti 4 GB</strong>&nbsp;được tối ưu h&oacute;a để cung cấp hiệu suất tốt nhất cho những t&aacute;c vụ nặng như render video, chơi game, hoạt động với phần mềm CAD v&agrave; 3D.</p>\r\n\r\n<p>&bull;&nbsp;Bộ nhớ&nbsp;<strong>RAM k&ecirc;nh đ&ocirc;i 16 GB</strong>&nbsp;cho ph&eacute;p m&aacute;y hoạt động trơn tru, chạy nhiều chương tr&igrave;nh v&agrave; t&aacute;c vụ c&ugrave;ng l&uacute;c m&agrave; kh&ocirc;ng gặp bất kỳ hiện tượng giật lag n&agrave;o, trải nghiệm chơi game v&agrave; chỉnh sửa video cũng sẽ trở n&ecirc;n mượt m&agrave; hơn.</p>\r\n\r\n<p>&bull;&nbsp;Ổ cứng&nbsp;<strong>SSD 512 GB NVMe PCIe</strong>&nbsp;với tốc độ đọc v&agrave; ghi dữ liệu nhanh gi&uacute;p cho laptop hoạt động ổn định hơn, giảm thời gian chờ v&agrave; cải thiện hiệu suất khi mở hoặc chạy chương tr&igrave;nh nặng.</p>\r\n\r\n<p>&bull; Với m&agrave;n h&igrave;nh&nbsp;<strong>15.6 inch</strong>&nbsp;độ ph&acirc;n giải&nbsp;<strong>Full HD (1920 x 1080)</strong>, h&igrave;nh ảnh hiển thị sẽ trở n&ecirc;n r&otilde; n&eacute;t v&agrave; ch&acirc;n thật hơn. C&ocirc;ng nghệ&nbsp;<strong>Anti Glare</strong>&nbsp;cũng gi&uacute;p giảm t&igrave;nh trạng hắt s&aacute;ng tr&ecirc;n m&agrave;n h&igrave;nh khi bạn sử dụng m&aacute;y trong m&ocirc;i trường &aacute;nh s&aacute;ng cao.</p>\r\n\r\n<p>&bull;&nbsp;<a href=\"https://www.dienmayxanh.com/laptop\" target=\"_blank\">Laptop</a>&nbsp;sử dụng tấm nền&nbsp;<strong>IPS</strong>&nbsp;với tần số qu&eacute;t&nbsp;<strong>144 Hz</strong>, cho bạn trải nghiệm khung h&igrave;nh mượt m&agrave; v&agrave; sắc n&eacute;t, những gi&acirc;y ph&uacute;t chơi game v&agrave; xem video sẽ trở n&ecirc;n tuyệt vời hơn bao giờ hết.</p>\r\n\r\n<p>&bull; Hệ thống loa được tinh chỉnh bởi c&aacute;c kỹ sư của&nbsp;<strong>B&amp;O</strong>&nbsp;kết hợp c&ugrave;ng c&ocirc;ng nghệ&nbsp;<strong>HP Audio Boost</strong>&nbsp;gi&uacute;p cải thiện chất lượng &acirc;m thanh, c&aacute;c dải &acirc;m sẽ được t&aacute;i tạo trung thực v&agrave; c&oacute; độ chi tiết cao, cho bạn trải nghiệm nghe nhạc hoặc xem video tốt hơn.</p>\r\n\r\n<p>&bull;&nbsp;<a href=\"https://www.dienmayxanh.com/laptop-hp-compaq\" target=\"_blank\">Laptop HP</a>&nbsp;được ho&agrave;n thiện với lớp&nbsp;<strong>vỏ nhựa</strong>&nbsp;sơn&nbsp;<strong>m&agrave;u xanh</strong>&nbsp;đậm cứng c&aacute;p, th&acirc;n m&aacute;y mang những đường n&eacute;t tinh tế đậm chất nam t&iacute;nh của d&ograve;ng sản phẩm VICTUS.</p>\r\n\r\n<p>&bull;&nbsp;<strong>Đ&egrave;n nền b&agrave;n ph&iacute;m</strong>&nbsp;sẽ hỗ trợ bạn g&otilde; ph&iacute;m chuẩn x&aacute;c hơn trong điều kiện &aacute;nh s&aacute;ng tối. Dọc hai b&ecirc;n m&aacute;y c&ograve;n c&oacute; nhiều cổng kết nối như: HDMI, Jack tai nghe 3.5 mm, SuperSpeed USB Type-C, LAN (RJ45) v&agrave; SuperSpeed USB A.</p>\r\n', '28290000', '2290000', 1, 0, 20, 'hp-victus-15-fa0111tx-i5-.jpg');

